icar.r:
\begin{itemize}
\item Affiche les caractères saisis à l'écran
\item Affiche des étoiles à la place des caractère lorsque l'affichage est protégé
\end{itemize}

\begin{itemize}
\item Lance la procédure d'identification (interrogation de la base de donnée).\\ 
Si valide : Affiche l'interface de sélection des configurations.\\ 
Sinon : Affiche "Identifiant/Mot de passe incorrect. Réesayez."
\end{itemize}

\begin{itemize}
\item Déconnecte l'utilisateur
\item Affiche la fenêtre de connexion
\end{itemize}

icar.c:
	\begin{enumerate}
	\item btn-creer-config
	\item tab-config
	\end{enumerate}
Clic

	\begin{itemize}
	\item Ajoute une ligne modifiable dans le tableau des configurations
	\item Propose la saisie du nom de la nouvelle configuration
	\item Grise les boutons : Créer, Supprimer, Renommer, Dupliquer
	\item Dégrise le bouton Valider
	\end{itemize}

icar.r:
\begin{itemize}
\item Grise les boutons : Créer, Supprimer, Renommer, Dupliquer
\item Dégrise le bouton Valider
\end{itemize}

\begin{itemize}
\item Propose la saisie du nom de la configuration sélectionnée
\item Grise les boutons : Créer, Supprimer, Renommer, Dupliquer
\item Dégrise le bouton Valider
\end{itemize}

\begin{itemize}
\item Ajoute une nouvelle configuration au tableau
\item Propose la saisie du nom de la configuration ajoutée
\item Grise les boutons : Créer, Supprimer, Renommer, Dupliquer
\item Dégrise le bouton Valider
\end{itemize}

\begin{itemize}
	\item Ferme la fenêtre de configuration
	\item Affiche la fenêtre de choix des configurations
\end{itemize}

icar.a:
Clic

			\begin{itemize}
			\item Ouvre une fenêtre permettant de sélectionner le chemin du nouveau fichier
			\item Enregistre la configuration courante dans le fichier sélectionné
			\end{itemize}

icar.i:
Séléctionner un élément

			\begin{enumerate}
			\item pannel-elements
			\item img-element
			\end{itemize}

		Clic

			\begin{itemize}
			\item Affiche les paramètres de l'éléments dans le panneau "Élément"
			\item Encadre l'élément sélectionné
			\end{itemize}

icar.r:
\begin{itemize}
\item Tourne la représentation graphique de l'élement
\end{itemize}

\begin{itemize}
\item Tourne la représentation graphique de l'élement
\end{itemize}

